using StandUpTracker.Services;namespace StandUpTracker.Tests.Services;/// <summary>/// Tests for ActivityMonitor - Main orchestrator integration/// NOTE: ActivityMonitor requires real service instances, not mocks, as it constructs them internally/// </summary>public class ActivityMonitorTests{    [Fact]    public void Constructor_CreatesInstance()    {        // Arrange        var idleTracker = new IdleTracker();        var windowTracker = new WindowTracker();        var dndService = new DoNotDisturbService();        var activityLogger = new ActivityLogger();        // Act        var sut = new ActivityMonitor(idleTracker, windowTracker, dndService, activityLogger);        // Assert        Assert.NotNull(sut);        Assert.Equal(AppState.Idle, sut.CurrentState);    }    [Fact]    public void Start_ExecutesWithoutError()    {        // Arrange        var idleTracker = new IdleTracker();        var windowTracker = new WindowTracker();        var dndService = new DoNotDisturbService();        var activityLogger = new ActivityLogger();        var sut = new ActivityMonitor(idleTracker, windowTracker, dndService, activityLogger);        // Act & Assert        sut.Start();        Assert.True(true);        sut.Dispose();    }    [Fact]    public void SetPaused_FiresPauseStateChangedEvent()    {        // Arrange        var idleTracker = new IdleTracker();        var windowTracker = new WindowTracker();        var dndService = new DoNotDisturbService();        var activityLogger = new ActivityLogger();        var sut = new ActivityMonitor(idleTracker, windowTracker, dndService, activityLogger);        var eventFired = false;        bool? pausedState = null;        sut.PauseStateChanged += (s, paused) =>        {            eventFired = true;            pausedState = paused;        };        // Act        sut.SetPaused(true);        // Assert        Assert.True(eventFired);        Assert.True(pausedState);        sut.Dispose();    }    [Fact]    public void Dispose_ExecutesWithoutError()    {        // Arrange        var idleTracker = new IdleTracker();        var windowTracker = new WindowTracker();        var dndService = new DoNotDisturbService();        var activityLogger = new ActivityLogger();        var sut = new ActivityMonitor(idleTracker, windowTracker, dndService, activityLogger);        sut.Start();        // Act & Assert        sut.Dispose();        Assert.True(true);    }}